var uglify = require('uglify-js');

var simplifyError = function (err) {
	var error;
	if (err.stack) {
		error = {
			message: err.message,
			stack: err.stack
		}
	} else {
		error = err;
	}
	return err;
};

var options = {
	fromString: true,
	mangle: true
};

process.on('message', function (m) {
	if (m.type == 'init') {
		if (m.data.mangle != null) {
			options.mangle = m.data.mangle;
		}
	} else if (m.type == 'minify') {
		var ack = {id: m.id};
		ack.type = 'ack';
		process.send(ack);
		
		var result = {id: m.id};
		try {
			result.type = 'result';
			result.data = uglify.minify(m.data, options).code;
		} catch (err) {
			result.type = 'error';
			result.data = simplifyError(err);
		}
		process.send(result);
	}
});